mapper.select:
SELECT ROW_NUMBER() OVER (ORDER BY AUTH_ID ASC) RNUM, AUTH_ID, AUTH_NM, AUTH_VAL, AUTH_ROLE, getCodeNm('AUTH_ROLE', AUTH_ROLE) as AUTH_ROLE_NM, ORD, BIGO, CREATED_AT, UPDATED_AT, UPDATED_ID, USE_YN, getCodeNm('USE_YN', USE_YN) as USE_YN_NM FROM AUTH_TB WHERE 1=1
AND USE_YN = #{use_yn}
AND AUTH_ROLE = #{auth_role}
OFFSET #{start_idx} limit #{page_per};

SELECT  COUNT(*)
FROM    AUTH_TB
WHERE   1=1

AND     USE_YN = #{use_yn}


AND     AUTH_ROLE = #{auth_role}

mapper.update:
UPDATE  AUTH_TB
SET     UPDATED_AT = now()

        , AUTH_NM = #{auth_nm}


        , AUTH_ROLE = #{auth_role}


        , ORD = #{ord}


        , BIGO = #{bigo}


        , USE_YN = #{use_yn}

WHERE   AUTH_ID = #{auth_id}

mapper.select:
SELECT  COUNT(*)
FROM    AUTH_TB
WHERE   AUTH_VAL = #{auth_val}
