mapper.select:
/* sql : BoardAdminDAO.findAll */ SELECT ROW_NUMBER() OVER (ORDER BY BOARD_ID ASC) RNUM, TITLE, BIGO, getcodenm('BOARD_TYPE', BOARD_TYPE) AS BOARD_TYPE_NM, getcodenm('USE_YN', USE_YN) AS USE_YN_NM, TO_CHAR(UPDATED_AT, 'YYYY-MM-DD HH24:MI') AS UPDATED_AT, getusernm(UPDATED_ID) AS UPDATED_NM, BOARD_ID FROM BOARD_TB WHERE 1=1
AND USE_YN = #{use_yn}
AND BOARD_TYPE = #{board_type}
AND UPPER(TITLE) LIKE '%'||UPPER(#{search_str})||'%'
OFFSET #{start_idx} limit #{page_per};

/* sql : BoardAdminDAO.countAll */
SELECT  COUNT(*)
FROM    BOARD_TB
WHERE   1=1

AND     USE_YN = #{use_yn}


AND     BOARD_TYPE = #{board_type}


AND     UPPER(TITLE) LIKE  '%'||UPPER(#{search_str})||'%'

mapper.update:
/* sql : BoardAdminDAO.updateBoard */
UPDATE  BOARD_TB
SET     UPDATED_AT = now()

        , TITLE = #{title}


        , UPDATED_ID = #{updated_id}


        , BIGO = #{bigo}


        , USE_YN = #{use_yn}

WHERE   BOARD_ID = #{board_id}
