mapper.select:
SELECT MENU_ID, MENU_NM, MENU_LV, PARENT_ID, MENU_URL, ORD, MENU_TYPE, USE_YN FROM MENU_TB WHERE MENU_LV = 1 AND AUTH_ROLE = #{auth_role}
AND MENU_ID = #{parent_id}
ORDER BY CAST(ORD AS INTEGER) ASC

mapper.update:
UPDATE 	MENU_TB
SET 	MAIN_YN = 'N'
WHERE 	AUTH_ROLE = #{auth_role}

UPDATE 	MENU_TB
SET 	UPDATED_AT = now()

        , MENU_NM = #{menu_nm}


        , MENU_URL = #{menu_url}


        , ORD = #{ord}


        , MENU_TYPE = #{menu_type}


        , USE_YN = #{use_yn}


        , MAIN_YN = #{main_yn}

WHERE 	MENU_ID = #{menu_id}
