mapper.select:
SELECT  COUNT(*)
FROM 	USER_TB
WHERE	LOGIN_ID = #{login_id}

mapper.update:
UPDATE USER_TB
SET UPDATED_AT = now()
  , USER_PW = #{user_pw}
WHERE LOGIN_ID = #{login_id}

mapper.select:
SELECT ROW_NUMBER() OVER (ORDER BY USER_ID DESC) RNUM, USER_ID, LOGIN_ID, EMAIL, USER_NM, CELL_PHONE, getCodeNm('USE_YN', USE_YN) as USE_YN_NM, USE_YN FROM USER_TB WHERE 1=1
AND (UPPER(LOGIN_ID) LIKE '%'||UPPER(#{search_str})||'%' OR UPPER(EMAIL) LIKE '%'||UPPER(#{search_str})||'%' OR UPPER(USER_NM) LIKE '%'||UPPER(#{search_str})||'%')
AND USE_YN = #{use_yn}
ORDER BY USER_ID DESC OFFSET #{start_idx} limit #{page_per};

SELECT COUNT(*) FROM USER_TB WHERE 1=1
AND (UPPER(LOGIN_ID) LIKE '%'||UPPER(#{search_str})||'%' OR UPPER(EMAIL) LIKE '%'||UPPER(#{search_str})||'%' OR UPPER(USER_NM) LIKE '%'||UPPER(#{search_str})||'%')
AND USE_YN = #{use_yn}

mapper.update:
UPDATE USER_TB
SET UPDATED_AT = now()

	, EMAIL = #{email}


	, USER_NM = #{user_nm}


	, USER_PW = #{user_pw}


	, CELL_PHONE = #{cell_phone}


	, USE_YN = #{use_yn}

WHERE USER_ID = #{user_id}
